mapper.select:
select board_id , user_id , board_title , board_content , board_reg_dt , board_read_count from board where board_title like CONCAT('%', #{searchWord} ,'%')
or user_id = (select user_id from users where user_name = #{searchWord})
limit #{limit} offset #{offset}

select board_id , user_id , board_title , board_content , board_reg_dt , board_read_count
  from board
 where board_id = #{board_id}

mapper.update:
update board
   set board_title = #{boardTitle},
   board_content = #{boardContent}
 where board_id = #{boardId}
